import React, { useState, useEffect } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import '../css/Form.css';
import Invitation from "../components/InviteToCompany";

const Form = () => {
    const { getAccessTokenSilently, user } = useAuth0();
    const [formData, setFormData] = useState({
        ssn: '',
        company: '',
        role: ''
    });
    const [applicationStatus, setApplicationStatus] = useState(null); // Track the application status
    const [isLoading, setIsLoading] = useState(true);
    const token = "eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCIsImtpZCI6Im8zWGhBVXk1cEsyTDZDekltZGZSTyJ9..FVl1FWmHZsZP-RwinwmJWlWebamE3eFxS5PvaXMORBVM23ItceTuCgqb2jhrYAMNBfPCMO_ZKJFUFlJ-86SzzH57QvIw_wRdpJEsl9zvRLh96cnqA3CJLawMnR7fM6suqHbSg3bkPbX9JKJi7FFzpSpRfz4SoG_1E_jmwErrgOGMWVHn8yPXWB_btNttW8kVGH8mE3fqOzbwFP2LSxz8u8xF9EpqEmnxTFptO88GjPGbEaC3Ubul0ekXdEI_tsBNn-ui6u6UxEDHRUOkQj724clKpZ8zzDf9wBR48QOVDpRUleBNQ8yc9tQNV8D5SRAp5pnGyDFpiV2dbaDmoFlzsQ";

    useEffect(() => {
        const getUserMetadata = async () => {
            setIsLoading(true);
            try {

                const response = await fetch(`https://poolcorp.authnworld.com/api/v2/users/${user.sub}`, {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });

                const userData = await response.json();
                setApplicationStatus(userData.user_metadata?.application);
            } catch (error) {
                console.error('Error loading user data:', error);
            } finally {
                setIsLoading(false);
            }
        };

        getUserMetadata();
    }, [getAccessTokenSilently, user?.sub]);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {

            const updatedData = {
                ...formData,
                application: 'submitted'
            };

            const response = await fetch(`https://poolcorp.authnworld.com/api/v2/users/${user.sub}`, {
                method: 'PATCH',
                headers: {
                    Authorization: `Bearer ${token}`,
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    user_metadata: updatedData
                })
            });

            if (!response.ok) {
                throw new Error(`Error: ${response.status}`);
            }

            alert('Profile and application status updated successfully');
        } catch (error) {
            console.error('Error updating profile and application status:', error);
        }
    };

    if (isLoading) {
        return <div>Loading...</div>;
    }

    if (applicationStatus === 'approved') {
        return <Invitation></Invitation>;

    }

    if (applicationStatus === 'pending') {
        return <div style={{ color: 'orange' }}>Your application is pending.</div>;
    }

    return (
        <div className="form-container" >
            <img src="https://devpoolcorpb2cstorage.blob.core.windows.net/poolcorpid/assets/images/pool360Icon.svg"
                alt="Pool Icon"
                className="form-icon" />
            <form onSubmit={handleSubmit} className="user-form">
                <label>
                    SSN:
                    <input type="text" name="ssn" value={formData.ssn} onChange={handleChange} />
                </label>
                <br />
                <label>
                    Company:
                    <input type="text" name="company" value={formData.company} onChange={handleChange} />
                </label>
                <br />
                <label>
                    Role:
                    <input type="text" name="role" value={formData.role} onChange={handleChange} />
                </label>
                <br />
                <button type="submit">Submit</button>
            </form>
        </div>
    );
};

export default Form;



